parameters.functionCode:
const response = $input.all()[0].json.result.data;

// Parse LLM response
const qualification = JSON.parse(response.content);

// Add metadata
return {
  json: {
    lead_id: $('Prepare Lead Data').item.json.lead_data.id,
    qualification: qualification,
    llm_metadata: {
      provider: response.provider,
      model: response.model,
      latency_ms: response.metadata.latencyMs,
      cached: response.metadata.cached,
      fallback_attempts: response.metadata.fallbackAttempts
    },
    qualified_at: new Date().toISOString()
  }
};

// Log success metrics
console.log(`✅ Lead qualified as HIGH PRIORITY`);
console.log(`Provider: ${$json.llm_metadata.provider}`);
console.log(`Model: ${$json.llm_metadata.model}`);
console.log(`Latency: ${$json.llm_metadata.latency_ms}ms`);
console.log(`Cached: ${$json.llm_metadata.cached}`);
console.log(`Fallback attempts: ${$json.llm_metadata.fallback_attempts}`);

return $input.all();

// Log medium/low priority
console.log(`⚡ Lead qualified as ${$json.qualification.priority.toUpperCase()}`);
console.log(`Provider: ${$json.llm_metadata.provider}`);

return $input.all();
